var Labels=new Class({Implements:[Events],initialize:function(){$$(".fabrikElementContainer").each(function(d){var b=d.getElement("label");if(typeOf(b)!=="null"){var a=d.getElement("input");if(typeOf(a)==="null"){a=d.getElement("textarea")}if(typeOf(a)!=="null"){a.value=b.innerHTML;a.addEvent("click",function(c){this.toogleLabel(c,a,b.innerHTML)}.bind(this));a.addEvent("blur",function(c){this.toogleLabel(c,a,b.innerHTML)}.bind(this));b.set("html","");d.getElement(".fabrikLabel").dispose()}}}.bind(this))},toogleLabel:function(c,a,b){new Event(c).stop();if(c.type==="click"){if(a.get("value")===b){a.value=""}}else{if(a.get("value")===""){a.value=b}}}});head.ready(function(){new Labels()});